create table tbl_video_info
(
    id               bigint(19)                 not null comment '唯一编号（UUID自动生成）'
        primary key,
    video_id         bigint(19)                 not null comment '视频ID',
    video_name       varchar(25)                not null comment '视频名称',
    video_duration   int(32)                    not null comment '视频时长',
    video_url        varchar(255)               null comment '视频链接',
    video_img        varchar(255)               null,
    video_industry   varchar(64)                not null,
    video_heat       double(20, 1)              null,
    streamer_id      bigint(19)                 not null comment '博主ID',
    likes            bigint                     not null comment '点赞数',
    comments         bigint                     not null comment '评论数',
    shares           bigint                     not null comment '分享数',
    music_name       varchar(64)                null,
    music_img_url    varchar(255)               null comment '视频背景音乐图片地址',
    music_used       bigint                     null,
    video_image_url  varchar(255)               null,
    release_time     datetime                   null comment '发布时间',
    playback_volume  bigint                     null,
    is_related_goods int(3)                     null,
    goods_id         varchar(64)                null,
    goods_influence  double(20, 2)              null,
    create_time      datetime                   null comment '创建时间',
    modified_time    datetime                   null comment '修改时间',
    deleted          tinyint unsigned default 0 null comment '逻辑删除标识（0为否，1为是）'
)
    charset = utf8;

