create table ums_admin_streamer
(
    id              bigint auto_increment
        primary key,
    host_name       varchar(45)                       null comment '主播名称',
    real_name       varchar(5)                        null comment '真实姓名',
    id_card         varchar(18)                       null comment '身份证号',
    certification   varchar(5)       default '未认证' null comment '认证状态（审核中、已认证、未认证）',
    image_id        bigint                            null comment '图片id',
    account_balance decimal(8, 2)    default 0.00     not null comment '账户余额',
    industry        varchar(64)                       null comment '所属行业',
    belong_to       bigint                            null comment '播主所属MCN，若为-1则为MCN机构，不是-1就是主播',
    platform        varchar(20)                       null comment '所属平台(抖音、快手、淘宝)
',
    streamer_desc   varchar(255)                      null comment '主播描述',
    create_time     datetime                          null comment '创建时间',
    modified_time   datetime                          null comment '修改时间',
    deleted         tinyint unsigned default 0        null comment '逻辑删除标识（0为否，1为是）'
)
    comment '播主/MCN 表' charset = utf8;

